/**
* Count characters in your string
* @link https://www.codewars.com/kata/52efefcbcdf57161d4000091
*/

function count(string) {
  return [...string].reduce(
    (obj, char) => ((obj[char] = (obj[char] || 0) + 1), obj),
    {}
  );
}

/**
* Counting Duplicates
* @link https://www.codewars.com/kata/54bf1c2cd5b56cc47f0007a1
*/

function duplicateCount(text) {
  const normalizeText = text.toLowerCase().replace(/\d/g, '');
  const uniqLetters = [...new Set(normalizeText)];

  return uniqLetters.filter(
    (letter) =>
      normalizeText.indexOf(letter) !== normalizeText.lastIndexOf(letter)
  ).length;
}

/**
* Simple Encryption #1 - Alternating Split
* @link https://www.codewars.com/kata/57814d79a56c88e3e0000786
*/

function encrypt(text, n) {
  if (!text || n < 0) return text;

  if (n === 0) {
    return text;
  } else {
    const oddPart = [...text].filter((_, i) => i % 2).join('');
    const evenPart = [...text].filter((_, i) => i % 2 === 0).join('');

    return encrypt(oddPart + evenPart, n - 1);
  }
}

function decrypt(encryptedText, n) {
  if (!encryptedText || n < 0) return encryptedText;

  if (n === 0) {
    return encryptedText;
  } else {
    const encryptedArr = [...encryptedText];
    const oddPart = encryptedArr.slice(0, Math.floor(encryptedArr.length / 2));
    const evenPart = encryptedArr.slice(Math.floor(encryptedArr.length / 2));
    let decryptedText = '';

    evenPart.forEach((v, i) => {
      decryptedText += v;

      if (oddPart[i]) {
        decryptedText += oddPart[i];
      }
    });

    return decrypt(decryptedText, n - 1);
  }
}

/**
* Sort the odd
* @link https://www.codewars.com/kata/578aa45ee9fd15ff4600090d
*/

function sortArray(array) {
  const clone = [...array];
  const odd = clone.filter((value) => value % 2).sort((a, b) => a - b);
  let i = 0;

  return clone.map((value) => {
    if (value % 2) {
      return odd[i++];
    }

    return value;
  });
}

/**
* Write Number in Expanded Form - Part 2
* @link https://www.codewars.com/kata/write-number-in-expanded-form-part-2
*/

function expandedForm(num) {
  return String(num)
    .split('.')
    .map((part, partIndex) => {
      return part
        .split('')
        .reverse()
        .map((v, i, arr) => {
          if (v === '0') return null;

          if (partIndex === 0) {
            return v * Math.pow(10, i);
          }

          return `${v}/${Math.pow(10, arr.length - i)}`;
        })
        .reverse()
        .filter((v) => v)
        .join(' + ');
    })
    .filter((v) => v)
    .join(' + ');
}
