import { send } from 'mail';

interface ProjectDetails {
  name: string;
  description: string;
  stack: string[];
  benefits: string[];
}

export const sendAwesomeProjectOffer = async (
  details: ProjectDetails
): void => {
  const { name } = details;

  await send({
    message: `Юра, а давай вместе приведем к успеху наш проект ${name} :)`,
    details,
  });
};

