import { send } from 'mail';

interface ProductDetails {
  name: string;
  description: string;
  stack: string[];
  benefits: (number | string)[];
}

export const sendAwesomeProductOffer = async (
  details: ProductDetails
): void => {
  const { name } = details;

  await send({
    message: `Юра, а давай вместе приведем к успеху наш продукт ${name} :)`,
    details,
  });
};

